import { useState } from 'react';
import './App.css';
import Cards from "./components/Cards/Cards.jsx";
import Navbar from './components/Nav/Nav.jsx';
import axios from "axios";
import { Route, Routes } from 'react-router-dom';
import About from './components/About/About.jsx';
import Detail from './components/Detail/Detail.jsx';
import Error from './components/Error/Error';


function App () {
  const [input, setInput] = useState("");
  const [characters,setCharacters] = useState([]);
  
  
  function changeHandler(e) {
    e.preventDefault();
    let inputValue = e.target.value;

    setInput(inputValue);
  }

  async function searchHandler(e) {
    e.preventDefault();

    let found = characters.find((character) => character.id === Number(input));

    // if (!found) {
    //     fetch(`https://rickandmortyapi.com/api/character/${input}`)
    //      .then((response) => response.json())
    //      .then((data) => {
    //        if (data.name) {
    //          setCharacters((oldChars) => [...oldChars, data]);
    //        } else {
    //           window.alert("No hay personajes con ese ID");
    //         }
    //       });
    //    } else {
    //     alert("el personaje ya fue agregado");
    //    }
    //   }
    if (!found) {
      let response = (
        await axios.get(`https://rickandmortyapi.com/api/character/${input}`)
      ).data;

      if (response.name) {
        setCharacters([...characters, response]);
      }
    } else {
      alert("el personaje ya fue agregado");
    }
  }

     const onClose = (id) => {
      const filtro = characters.filter((char) => char.id !== Number(id))
      setCharacters(filtro);
}
    
    
      function randomHandler() {
        let haveIt = [];
    
        let random = (Math.random() * 826).toFixed();
    
        random = Number(random);
    
        if (!haveIt.includes(random)) {
          haveIt.push(random);
          fetch(`https://rickandmortyapi.com/api/character/${random}`)
            .then((response) => response.json())
            .then((data) => {
              if (data.name) {
                setCharacters((oldChars) => [...oldChars, data]);
              } else {
                window.alert("No hay personajes con ese ID");
              }
            });
        } else {
          alert("Ya agregaste a todos los personajes");
        }
      
    }
  
  return (
    <div className='App'>
       
       <Navbar
         onSearch={searchHandler}
         onChange={changeHandler}
         random={randomHandler}
      />
      <Routes>
        <Route
         path='/home'
         element={<Cards characters={characters}onClose={onClose}/>}
          /> 
          <Route
          path='/about'
          element={<About/>}
          />
          <Route
          path='/detail/:detailID'
          element={<Detail/>}
          />
          <Route
        path='*'
        element={<Error/>}
        />
      </Routes>
        <Cards characters={characters} onClose={onClose} />
      </div>
  
  );
}

export default App;
