import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';



export default function Detail () {
  const { id } = useParams();
  const navigate = useNavigate();
  const [character, setCharacter] = useState({});
   

    function navegar() {
    navigate('/home');
  }
   
    useEffect(() => {
        let response = axios.get(`http://localhost:3001/rickandmorty/character/:${id}`)
          .then((response) => response.json())
          .then((char) => {
            if (char.name) {
               //  console.log(char)
              setCharacter(char); //{info personaje}
            } else {
              window.alert("No hay personajes con ese ID");
            }
          })
          .catch((err) => {
            window.alert("No hay personajes con ese ID");
          });
        return setCharacter({});
      }, [id]);

    return (
         <div>
            <button  onClick={navegar}  >Ir a Home</button>
         
            <h1>Name: {character.name}</h1>
            <div>
                <h2>STATUS: {character.status}</h2>
                <h2>ESPECIE: {character.species}</h2>
                <h2>GÃ‰NERO: {character.gender}</h2>
                <h2>ORIGEN: {character.origin?.name}</h2>
            </div>
            <img src={character.image} alt="img not found"/>
        </div>
    )
}
