    //     }
      
    // }
  
  return (
    <div className='App'>
       
       <Navbar
         onSearch={onSearch}
        // random={randomHandler}
      />
      <Routes>
        <Route
         path='/home'
         element={<Cards characters={characters}onClose={onClose}/>}
          /> 
          <Route
          path='/about'
          element={<About/>}
          />
          <Route
          path='/detail/:detailID'
          element={<Detail/>}
          />
          <Route
        path='*'
        element={<Error/>}
        />
      </Routes>
        <Cards characters={characters} onClose={onClose} />
      </div>
  
  );
}

export default App;
