   import style from './SearchBar.module.css';
import  { useState } from 'react';


export default function SearchBar(props) {
   const [character,setCharacter] = useState(0);
   const handleSearch = (evento) => {
      let { value } = evento.target;
      setCharacter(value);
   };
   return (
      <div className={style.bar}>
          <input className={style.input} type='search'placeholder='Buscar...'onChange={handleSearch} />
      <button className={style.boton} onClick={() => props.onSearch(character)} placeholder={style.placeholder}>Agregar</button>
      </div>
   )
}
